<?php // Importing DBConfig.php file.include 'DBConfig.php'; // Connecting to MySQL Database. $con = mysqli_connect($HostName,$HostUser,$HostPass,$DatabaseName);  // Getting the received JSON into $json variable. $json = file_get_contents('php://input');  // decoding the received JSON and store into $obj variable. $obj = json_decode($json,true);  // Populate Note from JSON $obj array and store into $note. $note = $obj['Note'];  // Populate Title from JSON $obj array and store into $title. $title = $obj['Title'];  $noteId = $obj['ID'];   // Creating SQL query and insert the record into MySQL database table. $Sql_Query = "insert into notes (note,title,userId) values ('$note','$title','$noteId')";   if(mysqli_query($con,$Sql_Query)){  // If the record inserted successfully then show the message.$MSG = 'Note Added.' ; // Converting the message into JSON format.$json = json_encode($MSG); // Echo the message. echo $json ;  } else{  echo 'Try Again';  } mysqli_close($con);?>