<?php // Importing DBConfig.php file.include 'DBConfig.php'; // Connecting to MySQL Database. $con = mysqli_connect($HostName,$HostUser,$HostPass,$DatabaseName);  // Getting the received JSON into $json variable. $json = file_get_contents('php://input');  // decoding the received JSON and store into $obj variable. $obj = json_decode($json,true);  // Populate First Name from JSON $obj array and store into $firstName. $firstName = $obj['FirstName'];  // Populate Last Name from JSON $obj array and store into $lastName. $lastName = $obj['LastName'];  // Populate userName from JSON $obj array and store into $userName. $userName = $obj['Username'];  // Populate Password from JSON $obj array and store into $password. $password = $obj['Password'];  // Creating SQL query and insert the record into MySQL database table. $Sql_Query = "insert into users (firstName,lastName,userName,password) values ('$firstName','$lastName','$userName','$password')";   if(mysqli_query($con,$Sql_Query)){  // If the record inserted successfully then show the message.$MSG = 'Registration Successful.' ; // Converting the message into JSON format.$json = json_encode($MSG); // Echo the message. echo $json ;  } else{  echo 'Try Again';  } mysqli_close($con);?>