<?php // Importing DBConfig.php file.include 'DBConfig.php'; // Connecting to MySQL Database. $con = mysqli_connect($HostName,$HostUser,$HostPass,$DatabaseName);  // Getting the received JSON into $json variable. $json = file_get_contents('php://input');  // decoding the received JSON and store into $obj variable. $obj = json_decode($json,true);  // Populate Username from JSON $obj array and store into $userName. $userName = $obj['Username'];  // Populate password from JSON $obj array and store into $password. $password = $obj['Password'];  // Creating SQL query and insert the record into MySQL database table. $Sql_Query = "SELECT * FROM users WHERE userName='$userName' AND password='$password'";  //$selectId = $conn->query(""); $check = mysqli_fetch_array(mysqli_query($con,$Sql_Query));  if(isset($check)){	$det = $con->query("SELECT * FROM users WHERE userName='$userName' AND password='$password'")->fetch_assoc();;	 $uid = $det['userId'];  // If the record was found then show the message.//$MSG = "Data Matched";$MSG = $uid; // Converting the message into JSON format.$json = json_encode($MSG); // Echo the message. echo $json ;  } else{  echo 'Try Again';  } mysqli_close($con);?>